import { useQuery, useQueryClient } from "@tanstack/react-query";
import { getAllUserFortune } from "@/apis/luck/luckAPI";

const useAllUserFortune = () => {
  const queryClient = useQueryClient();

  const { data } = useQuery(["allUserFortune"], () => getAllUserFortune(), {
    // initialData를 사용하여 이미 데이터가 캐시되어 있는 경우 요청을 보내지 않음
    initialData: queryClient.getQueryData(["allUserFortune"]),
    // stale 옵션을 false로 설정하여 항상 캐시된 데이터를 사용
    stale: false,
  });

  return data;
};

export { useAllUserFortune };
