import React, { useEffect, useState, useMemo } from "react";
import styled from "styled-components";
import Swiper, { Navigation, Pagination } from "swiper"; // Import Swiper and necessary modules
import RegistBtn from "@/components/registbtn";
import { useAllCreditCard } from "@/apis/dictionary/Queries/useAllCreditCard";

import foodIcon from "@/assets/img/foodIcon.png";
import moneyIcon from "@/assets/img/moneyIcon.png";

const StyledMain = styled.main`
  position: relative;
  min-height: 100vh;
  column-gap: 3rem;
  padding-block: min(20vh, 3rem);
  padding-inline: 2.3em;
  align-items: center;
  justify-content: center;
  background: #461356;
  overflow: hidden;

  @media screen and (min-width: 960px) {
    display: flex;
    padding-inline: 0;
  }
`;

const StyledContent = styled.div`
  width: 100%;
  color: #fff;
  position: relative;
  z-index: 2;
  margin-bottom: 3em;

  @media screen and (min-width: 960px) {
    width: 40%;
    padding-left: 5em;
    margin-bottom: 0;
  }
`;

const StyledTitle = styled.h2`
  font-family: "Pyeongchangpeace";
  text-align: left;
  letter-spacing: 0.8px;
  font-size: 2.8rem;
  line-height: 1.2;
`;

const StyledDate = styled.p`
  font-family: "Pyeongchangpeace";
  text-align: left;
  color: #957796;
  letter-spacing: 0.8px;
  font-size: 1.5rem;
  line-height: 2;
`;

const StyledCardName = styled.div`
  font-size: 2rem;
  text-align: left;
  line-height: 1;
  width: fit-content;
  margin-bottom: 15px;
`;

const CardNameText = styled.div`
  background: linear-gradient(to top, #813e83 100%, transparent 50%);
  font-weight: 700;
`;

const StyledCardCompany = styled.p`
  text-align: left;
  line-height: 1;
  margin-bottom: 30px;
`;

const StyledCardCondition = styled.p`
  font-size: 1.5rem;
  text-align: left;
  line-height: 1;
  margin-bottom: 30px;
`;

const StyledCardBenefits = styled.p`
  font-size: 1.3rem;
  text-align: left;
  line-height: 1;
`;

const StyledCardAlarm = styled.p`
  font-size: 1rem;
  text-align: left;
  line-height: 1.8;
  margin-top: 10px;
  margin-bottom: 30px;
`;

const StyledSwiperContainer = styled.div`
  position: relative;
  overflow: hidden;
  width: 100%;
  right: 0px;
  margin: 0 auto;

  @media screen and (min-width: 960px) {
    width: 60%;
    right: -60px;
  }
`;

const StyledSwiper = styled.div`
  position: relative;
  width: 100%;
  z-index: 2;
`;

const StyledSwiperSlide = styled.div`
  width: 10rem;
  height: 24rem;
  display: flex;
  flex-direction: column;
  align-items: self-start;
  position: relative;
  border-radius: 12px;
  box-shadow: -1px 5px 15px #0000001f;
  text-align: center;
  opacity: 0.4;
  transition: opacity 0.4s ease-in;

  span {
    display: inline-block;
    background: #9926e1;
    border-radius: 0 50px 50px 0;
    text-transform: capitalize;
    padding: 12px 20px;
    letter-spacing: 0.5px;
    font-weight: 500;
    position: absolute;
    top: 2em;
    left: 0;
    color: #fff;
  }

  h3 {
    color: #fff;
    font-size: 1.3rem;
    line-height: 1.4;
    margin-bottom: 0.625rem;
    letter-spacing: 0.8px;
    position: relative;
    word-wrap: break-word;
  }

  @media screen and (min-width: 800px) {
    h3 {
      font-size: 1.8rem;
    }
  }

  p {
    color: #fff;
    line-height: 1.6;
    font-size: 0.8rem;
  }

  .slide-content {
