import { useQuery } from "@tanstack/react-query";
import { getConsumeRecent } from "@/apis/spend/spendAPI";
import { useMemo } from "react";

const useGetConsumeRecent = (nowDate: string) => {
  // useMemo를 사용하여 쿼리를 생성합니다.
  const query = useMemo(() => {
    // 이 부분에서 getConsumeRecent 함수를 호출하고 결과를 반환합니다.
    return getConsumeRecent(nowDate);
  }, [nowDate]); // nowDate가 변경될 때마다 이 쿼리가 재생성됩니다.

  // 쿼리 실행
  const { data } = useQuery(["consumeRecent", nowDate], query, {
    enabled: Boolean(nowDate),
    suspense: false,
  });

  return data;
};

export { useGetConsumeRecent };
