import React from "react";
import styled, { keyframes } from "styled-components";

const data = [
  {
    category: "식품",
    cost: 50000,
    analytics: "테스트1",
  },
  {
    category: "여행",
    cost: 1000000,
    analytics: "테스트2",
  },
  {
    category: "문화",
    cost: 247000,
    analytics: "테스트3",
  },
];

const SliderContainer = styled.div`
  background: #461356;
  height: 100px;
  margin: auto;
  overflow: hidden;
  position: relative;
  width: 960px;

  &::before,
  &::after {
    content: "";
    height: 100px;
    position: absolute;
    width: 200px;
    z-index: 2;
  }

  &::after {
    right: 0;
    top: 0;
    transform: rotateZ(180deg);
  }

  &::before {
    left: 0;
    top: 0;
  }
`;

const SlideTrack = styled.div`
  animation: ${({ trackWidth }) => slideAnimation(trackWidth)} 10s linear
    infinite;
  display: flex;
  width: ${({ trackWidth }) => `${trackWidth}px`};
`;

const Slide = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100px;
  width: ${({ width }) => `${width}px`};
`;

function MonthSpendCarousel() {
  const totalCost = data.reduce((acc, item) => acc + item.cost, 0);
  const trackWidth = data.length * 250;
  const animationDuration = Math.max(10, totalCost / 100000); // Adjust this as needed

  const slideAnimation = (width) => keyframes`
    0% {
      transform: translateX(0);
    }
    100% {
      transform: translateX(calc(-${width}px * 7));
    }
  `;

  return (
    <SliderContainer>
      <SlideTrack trackWidth={trackWidth}>
        {data.map((item, idx) => (
          <Slide key={idx} width={(item.cost / totalCost) * trackWidth}>
            {item.category} {item.cost}
          </Slide>
        ))}
      </SlideTrack>
    </SliderContainer>
  );
}

export default MonthSpendCarousel;
