import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";

const data = [
  {
    category: "식품",
    cost: 50000,
    analytics: "테스트1",
  },
  {
    category: "여행",
    cost: 1000000,
    analytics: "테스트2",
  },
  {
    category: "문화",
    cost: 247000,
    analytics: "테스트3",
  },
  {
    category: "문화",
    cost: 247000,
    analytics: "테스트3",
  },
  {
    category: "문화",
    cost: 247000,
    analytics: "테스트3",
  },
  {
    category: "문화",
    cost: 247000,
    analytics: "테스트3",
  },
  {
    category: "문화",
    cost: 247000,
    analytics: "테스트3",
  },
];

const SliderContainer = styled.div`
  background: #461356;
  height: 80px;
  margin: auto;
  overflow: hidden;
  position: relative;
  width: 960px;

  &::before,
  &::after {
    content: "";
    height: 100px;
    position: absolute;
    width: 200px;
    z-index: 2;
  }

  &::after {
    right: 0;
    top: 0;
    transform: rotateZ(180deg);
  }

  &::before {
    left: 0;
    top: 0;
  }
`;

const SlideTrack = styled.div`
  display: flex;
  width: 100%;
  animation: ${(props) => slideAnimation(props.slideCount)} 10s linear infinite;
`;

const Slide = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100px;
  width: 250px;
`;

function slideAnimation(slideCount) {
  const percentage = (100 / slideCount).toFixed(2);
  let keyframe = "0% { transform: translateX(0); }";
  for (let i = 1; i <= slideCount; i++) {
    keyframe += `${percentage * i}% { transform: translateX(-${
      percentage * i
    }%); }`;
  }
  return keyframes`
    ${keyframe}
    100% { transform: translateX(-100%); }
  `;
}

function MonthSpendCarousel() {
  const [slideCount, setSlideCount] = useState(data.length);

  useEffect(() => {
    // 데이터 길이에 따라 슬라이드 개수 설정
    setSlideCount(data.length);
  }, []);

  return (
    <SliderContainer>
      <SlideTrack slideCount={slideCount}>
        {data.map((item, idx) => (
          <Slide key={idx}>
            {item.category} {item.cost}
          </Slide>
        ))}
      </SlideTrack>
    </SliderContainer>
  );
}

export default MonthSpendCarousel;
