import { useQuery } from "@tanstack/react-query";
import { useMemo } from "react";
import { getConsumeRecent } from "@/apis/spend/spendAPI";

const useGetConsumeRecent = (nowDate: string) => {
  const queryKey = ["consumeRecent", nowDate];

  // useMemo를 사용하여 queryFn을 생성합니다.
  const queryFn = useMemo(() => {
    // getConsumeRecent 함수를 호출하고 결과를 반환합니다.
    return async () => {
      const response = await getConsumeRecent(nowDate);
      return response.data;
    };
  }, [nowDate]);

  // 쿼리 실행
  const { data } = useQuery(queryKey, queryFn, {
    enabled: Boolean(nowDate),
    suspense: false,
  });

  return data;
};

export { useGetConsumeRecent };
