import React from "react";
import styled, { keyframes } from "styled-components";

const data = [
  {
    category: "식품",
    cost: 50000,
    analytics: "테스트1",
  },
  {
    category: "여행",
    cost: 1000000,
    analytics: "테스트2",
  },
  {
    category: "문화",
    cost: 247000,
    analytics: "테스트3",
  },
];

const slideAnimation = keyframes`
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(calc(-250px * 7));
  }
`;

const SliderContainer = styled.div`
  background: #461356;
  height: 100px;
  margin: auto;
  overflow: hidden;
  position: relative;
  width: 960px;

  &::before,
  &::after {
    content: "";
    height: 100px;
    position: absolute;
    width: 200px;
    z-index: 2;
  }

  &::after {
    right: 0;
    top: 0;
    transform: rotateZ(180deg);
  }

  &::before {
    left: 0;
    top: 0;
  }
`;

const SlideTrack = styled.div.attrs(({ itemCount }) => ({
  style: {
    width: `${250 * itemCount}px`, // Dynamic width based on item count
  },
}))`
  animation: ${slideAnimation} 10s linear infinite;
  display: flex;
`;

const Slide = styled.div.attrs(({ cost }) => ({
  style: {
    width: `${cost}px`, // Dynamic width based on cost
  },
}))`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100px;
`;

function MonthSpendCarousel() {
  const totalCost = data.reduce((acc, item) => acc + item.cost, 0);

  return (
    <SliderContainer>
      <SlideTrack itemCount={data.length}>
        {data.map((item, idx) => (
          <Slide key={idx} cost={(item.cost / totalCost) * 960}>
            {item.category} {item.cost}
          </Slide>
        ))}
      </SlideTrack>
    </SliderContainer>
  );
}

export default MonthSpendCarousel;
