import React, { useState, useEffect } from "react";
import Book from "@/components/book";
import styled from "styled-components";
import PokemonCard from "@/components/Pokemon";
import TarotCard from "@/components/tarotCard";
import DcitBtn from "@/components/dictBtn";
import frontcard from "@/assets/img/tarotCard/tarotCardFront.png";
import background from "@/assets/img/background/silkBackground.jpg";
import ChallengeBubble from "@/components/challengeBubble";
import { StyledChallengeBubbleProps } from "@/types/challengeType";
import ChallegeSuccess from "@/assets/img/challengeBubble/challengeSuccess.png";
import ShareModal from "@/components/shareModal";
import { useRecoilValue, useSetRecoilState } from "recoil";
import {
  creditInfoState,
  specialIdState,
  specialImageUrlState,
  spendIdState,
} from "@/states/dictionaryState";
import { useNavigate } from "react-router-dom";
import { useProfileImage } from "@/apis/menu/Mutations/useProfileImage";
import { toast } from "react-toastify";
import Lottie from "lottie-react";

import cardBack from "@/assets/img/tarotCard/tarotCardBack.png";

import { useChallengeCard } from "@/apis/dictionary/Queries/useChallengeCard";
import { useAllUserFortune } from "@/apis/luck/Queries/useAllUserFortune";
import { useGetAllConsume } from "@/apis/dictionary/Queries/useGetAllConsume";
import SpecialSelectModal from "@/components/specialSelectModal";
import card from "@/assets/img/cardFront.png";

interface Challenge {
  id: number;
  isSuccess: boolean;
  name: string;
  achievementCondition: string;
  startTime: string;
  endTime: string;
}

interface FortuneListProps {
  id: number;
  name: string;
  imagePath: string;
  summary: string;
  description: string;
}

interface AllConsumeProps {
  id: number;
  name: string;
  imagePath: string;
  createdDate: string;
  cardType: string;
}

const PageContainer = styled.div`
  position: fixed;
  display: flex;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-image: url(${background});
  background-size: cover;
`;

const ModalContainer = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  padding: 20px;
  display: ${(props) => (props.isOpen ? "block" : "none")};
  z-index: 3;
`;

const Backdrop = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8); /* Semi-transparent black background */
  display: ${(props) => (props.isOpen ? "block" : "none")};
  z-index: 1;
`;

const BookSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 50%;
  height: 100%;
`;

const BookTitle = styled.div`
  font-family: "Pyeongchangpeace";
  color: #fdf1f1;
  font-size: 60px;
  padding: 42px;
`;

const ContentSection = styled.div`
  width: 50%;
  height: 100%;
  padding-left: 20px;
  margin-top: 5%;
  overflow: auto;
  margin-bottom: 300px;
`;

const SpecialHeader = styled.div`
  width: 100%;
  text-align: left;
  margin-left: 20px;
`;

const SpecialText = styled.div`
  display: flex;
  align-items: center;
  font-family: "Pyeongchangpeace";
  text-align: left;
  color: #d39090;
  font-size: 36px;
  margin-bottom: 10px;
`;

const BasicText = styled.div`
  display: flex;
  align-items: center;
  font-family: "Pyeongchangpeace";
  text-align: left;
  color: #d39090;
  font-size: 24px;
  margin-bottom: 10px;
`;

const DictionaryPage = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isSpecialModalOpen, setIsSpecialModalOpen] = useState(false);
  const specialId = useRecoilValue(specialIdState);
  const profileImage = useProfileImage();

  const currentDate = new Date();
  currentDate.setHours(0, 0, 0, 0); // Set hours, minutes, seconds, and milliseconds to zero
  const initialEndDate = currentDate.toISOString().slice(0, -2);

  const setCreditInfo = useSetRecoilState(creditInfoState);
  const setSpendId = useSetRecoilState(spendIdState);
  const setSpecialImageUrl = useSetRecoilState(specialImageUrlState);

  const challengeCard = useChallengeCard(specialId);
  setSpecialImageUrl(challengeCard.card.imagePath);
  // const allUserFortune = useAllUserFortune().fortuneList;
  const allUserFortune = [
    {
      id: 1,
      name: "THE MAGICIAN",
      imagePath: "https://sonagi.site/outputs/fortune_01_202310031731.png",
      summary: "일개미처럼 열심히 일한다면 보람찰 거예요.",
      description:
        "오늘은 재물 운이 조금씩 움직이기 시작하는 날이에요. 아직 큰 수입을 기대하기에는 이르지만, 그래도 장래적인 관점에서 보면 꽤 중요한 시점이라고 할 수 있겠네요. 오늘은 자신의 능력을 키워나가는 시간을 가지는 것이 좋아 보여요. 너무 서두르지 말고 천천히, 그리고 꾸준히 노력하면 결과가 따라올 거예요.\\n오늘을 기점으로 당신의 미래를 위한 투자를 시작해 보세요. 물론, 오늘 급하게 큰 돈을 벌기를 바라는 것은 조금 이르다는 것을 명심하세요. 조금씩 천천",
    },
    {
      id: 2,
      name: "THE MOON",
      imagePath: "https://sonagi.site/outputs/fortune_02_202310031749.png",
      summary: "당신이 할 수 있는 만큼만 하기를 바라요.",
      description:
        "오늘은 금전 거래에 대해 좀 더 신중하게 접근하는 것이 좋을 것 같아요. 만일 누군가에게 돈을 빌려주게 된다면, 꽤 오랫동안 돌려받지 못할 가능성이 있습니다. 자칫 영영 돌려받지 못할 수도 있겠군요. 아무리 신뢰할 수 있는 사람이라도, 오늘은 돈 거래를 피하는 것이 바람직해 보입니다. 반대로 당신이 금전적인 지원이 필요한 상황이라도, 대출이나 빌려오기도 어려울 수 있어요. 당신의 신용도가 좋아도 서류 문제 등 미비한 점들이 발생할 확률이 높아 대출받는 ",
    },
    {
      id: 3,
      name: "THE TOWER",
      imagePath: "https://sonagi.site/outputs/fortune_03_202310040039.png",
      summary: "생각지도 못했던 곳에 갑자기 돈을 쓸 수 있어요.",
      description:
        "오늘은 예기치 못한 지출이 발생할 수 있는 날이네요. 갑작스런 핸드폰 고장 또는 급하게 필요한 물품 구입과 같은 사건들이 예상 외로 발생할 수 있어요. 이런 상황에 대비해 일상 속에서 좀 더 절약하는 습관을 길러 보세요. 예를 들어, 불필요한 소비를 줄이고, 적당한 금액의 비상금을 마련해 두는 것이 좋겠죠.\\n비록 오늘 아쉬운 지출이 있을지라도, 다행히 큰 문제나 사고는 피할 수 있을 것으로 보입니다. 그러니 너무 걱정하지 마세요. 아무리 작은 금액이라",
    },
    {
      id: 4,
      name: "THE DEVIL",
      imagePath: "https://sonagi.site/outputs/fortune_04_202310040051.png",
      summary: "물도 이렇게 마구 쓰진 않을텐데 반성하세요.",
      description:
        "오늘은 자신도 모르게 '흥청망청' 돈을 쓸 가능성이 높은 날이에요. 특히나 친구들과의 술자리에서 분위기에 휩쓸리면, 지출이 예상보다 늘어날 수 있어요. 이런 날은 재미있고 즐거운 시간을 보낼 수 있지만, 나중에 지출을 회상하며 후회하는 일이 없도록 주의가 필요해요. 그러니 오늘은 지출을 조절하는 것을 명심하세요. 이런 날에는 집에서 편안하게 시간을 보내는 것도 나쁘지 않은 선택이 될 수 있습니다. \\n현명한 결정을 내리시길 바라며, 오늘도 행운이 함께하",
    },
    {
      id: 5,
      name: "WHEEL OF FORTUNE",
      imagePath: "https://sonagi.site/outputs/fortune_05_202310040059.png",
      summary: "통장 잔고가 올라가는 걸 보며 뿌듯하겠어요.",
      description:
        "오늘은 어디선가 예상치 못한 금전적인 행운이 찾아올 수 있는 날입니다! 기대하던 적금 만기가 도래했거나, 오늘은 그동안 미루었던 환급 신청을 해볼 좋은 시간이에요. 소소하게 샀던 복권도 의외의 행운을 가져다줄지 모르겠네요. 그러나, 이런 행운을 누릴 때는 욕심을 부리지 않는 것이 중요해요. 갑작스럽게 생긴 금전적인 여유를 흥청망청 소비하지 않도록 주의하세요. 현명한 선택으로 더 좋은 금융 상품에 투자하거나, 청약 저축을 시작하는 것도 고려해보세요. \\n",
    },
    {
      id: 6,
      name: "ACE OF PENTACLES",
      imagePath: "https://sonagi.site/outputs/fortune_06_202310040952.png",
      summary: "금전이 뒷받침되는 하루라니 부러워요",
      description:
        "오늘은 기대하지 않던 금전적인 기회가 찾아올 수 있는 특별한 날이네요! 아마도 새로운 아르바이트 기회 혹은 다른 수입 창구에서 긍정적인 변화가 있을 것으로 보입니다. 주변에서 부탁하는 일이 있다면, 도와주는 것도 행운을 불러올 수 있는 기회가 될 수 있어요. 평소에 생각하지 못했던 방법으로도 소소한 수입을 창출해 볼 수 있는 좋은 시간입니다. 애물단지에 있던 물건을 중고 시장에 내놓는 것도 하나의 방법이 될 수 있어요. \\n오늘의 키워드는 '유연성'입니",
    },
    {
      id: 7,
      name: "THE EMPRESS",
      imagePath: "https://sonagi.site/outputs/fortune_07_202310041026.png",
