import { atom } from "recoil";
import { persistAtom } from "recoil-persist";

interface LuckInfo {
  name: string;
  summary: string;
  imagePath: string;
  description: string;
}

interface ColorInfo {
  color: string;
  colorName: string;
  description: string;
  imagePath: string;
}

const defaultUserInfo: LuckInfo = {
  name: "",
  summary: "",
  imagePath: "",
  description: "",
};

const defaultColorInfo: ColorInfo = {
  color: "",
  colorName: "",
  description: "",
  imagePath: "",
};

export const luckInfoState = atom<LuckInfo>({
  key: "luckInfoState",
  default: defaultUserInfo,
  effects_UNSTABLE: [persistAtom], // Apply the persistAtom effect
});

export const colorInfoState = atom<ColorInfo>({
  key: "colorInfoState",
  default: defaultColorInfo,
  effects_UNSTABLE: [persistAtom], // Apply the persistAtom effect
});
