import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { getAllConsume } from "@/apis/dictionary/dictionaryAPI";

const useGetAllConsume = (startDate: string) => {
  const currentDate = new Date(); // 현재 시간을 얻습니다.
  const initialEndDate = currentDate.toISOString().slice(0, -2); // 현재 시간을 ISO 형식으로 변환합니다.

  // endDate를 상태로 관리합니다.
  const [endDate, setEndDate] = useState(initialEndDate);

  const queryKey = ["AllConsume", startDate, endDate];

  const fetchData = async () => {
    const result = await getAllConsume(startDate, endDate);
    return result;
  };

  const { data } = useQuery(queryKey, fetchData);

  return data;
};

export { useGetAllConsume };
