import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { getConsumeRecent, updateDiary } from "@/apis/spend/spendAPI";
import { nowDateInfoState } from "@/states/spendState";
import { useRecoilState } from "recoil";

const useGetConsumeRecent = (nowDate: string) => {
  const queryKey = ["consumeRecent", nowDate];
  const queryClient = useQueryClient();

  const { data } = useQuery(
    queryKey,
    () => {
      return getConsumeRecent(nowDate);
    },
    {
      enabled: Boolean(nowDate),
      suspense: false,
    }
  );

  // 필요한 경우 query 객체를 재사용하려면 queryClient를 사용할 수 있습니다.
  const refetchConsumeRecent = () => {
    queryClient.invalidateQueries(queryKey);
  };

  return { data, refetchConsumeRecent };
};
