import React, { FC, useEffect, useState } from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import DashboardLayout from "@/layouts/DashboardLayout";
import LoadingPage from "@/pages/loading";

type DashboardLayout = /*unresolved*/ any;

interface RouteType {
  path: string;
  layout?: FC<DashboardLayout>;
  element: React.LazyExoticComponent<FC>;
}

const routes: RouteType[] = [
  {
    path: "/",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/landing")),
  },
  {
    path: "/main",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/main")),
  },
  {
    path: "/crystal",
    element: React.lazy(() => import("@/pages/crystalBall")),
  },
  {
    path: "/profile",
    element: React.lazy(() => import("@/pages/profile")),
  },
  {
    path: "/menu",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/menu")),
  },
  {
    path: "/faq",
    element: React.lazy(() => import("@/pages/faq")),
  },
  {
    path: "/luck",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/luck")),
  },
  {
    path: "/luckcontent",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/luckContent")),
  },
  {
    path: "/landing",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/landing")),
  },
  {
    path: "/oauth/redirect",
    element: React.lazy(() => import("@/pages/redirect")),
  },
  {
    path: "/dictionary",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/dictionary")),
  },
  {
    path: "/spend",
    layout: DashboardLayout,
    element: React.lazy(() => import("@/pages/spendPattern")),
  },
  {
    path: "/next",
    element: React.lazy(() => import("@/pages/nextSpendPattern")),
  },
  {
    path: "/card",
    element: React.lazy(() => import("@/pages/cardRecommend")),
  },
  {
    path: "/loading",
    element: React.lazy(() => import("@/pages/loading")),
  },
  // {
  //   path: "/",
  //   element: () => <Navigate replace to="/main" />,
  // },
  {
    path: "/*",
    element: React.lazy(() => import("@/pages/404")),
  },
];

const RenderRoutes = () => {
  const [sseData, setSSEData] = useState(null);

  useEffect(() => {
    const eventSourceOptions: EventSourceInit = {
      // 필요한 옵션을 설정할 수 있습니다.
      withCredentials: true,
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${
          localStorage.getItem("accessToken") || undefined
        }`,
      },
    };

    // Authorization 헤더를 추가하려면 서버에서 처리해야 합니다.
    const eventSource = new EventSource(
      "https://masoori.site/api/sse/send-data",
      eventSourceOptions
    );

    eventSource.onmessage = (event) => {
      console.log(event);
      const data = JSON.parse(event.data);
      setSSEData(data);
    };

    return () => {
      eventSource.close();
    };
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("https://masoori.site/api/sse/send-data", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${
              localStorage.getItem("accessToken") || ""
            }`,
          },
        });

        if (!response.ok) {
          throw new Error("Network response was not ok");
        }

        const data = await response.json();
        setSSEData(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();

    return () => {
      eventSource.close();
    };
  }, []);

  return (
    <React.Suspense
      fallback={
        <div>
          <LoadingPage />
        </div>
      }
    >
      <Routes>
        {routes.map((route, i) => {
          const RouteElement = route.element;
          const RouteLayout = route.layout || React.Fragment;
          //   const Guard = route.guard || React.Fragment;

          return (
            <Route
              key={i}
              path={route.path}
              element={
                <RouteLayout>
                  <RouteElement />
                </RouteLayout>
              }
            />
          );
        })}
      </Routes>
    </React.Suspense>
  );
};

export default RenderRoutes;
